const { readFileSync } = require(''fs'');
const { resolve } = require(''path'');
const { createClient } = require(''@supabase/supabase-js'');

const envContent = readFileSync(resolve(''.env.local''), ''utf-8'');
envContent.split(''\n'').forEach(line => {
  const trimmed = line.trim();
  if (trimmed && !trimmed.startsWith(''#'')) {
    const eqIdx = trimmed.indexOf(''='');
    if (eqIdx > 0) {
      const key = trimmed.substring(0, eqIdx).trim();
      const val = trimmed.substring(eqIdx + 1).trim().replace(/^[""'']|[""'']$/g, '''');
      process.env[key] = val;
    }
  }
});

const supabase = createClient(process.env.NEXT_PUBLIC_SUPABASE_URL, process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY);

(async () => {
  const { data: salaryExpenses } = await supabase
    .from(''expenses'')
    .select(''id, date, amount, description, is_cleared'')
    .ilike(''description'', ''%Salary%'')
    .order(''date'', { ascending: false })
    .limit(10);

  console.log(''Recent salary-related expenses:'');
  console.log(JSON.stringify(salaryExpenses, null, 2));
})();
")
